'use client';

import React, { useState } from 'react';

import { Upload, Image, notification } from 'antd';
import { DeleteOutlined, EditOutlined } from '@ant-design/icons';

import { useParams } from 'next/navigation';
import { scaleDownImage } from '@/lib/helpers/imageHelpers';
import { useEnvironment } from '@/components/auth-can';

type ImageSelectionSectionProperties = {
  imageFileName?: string;
  onImageUpdate: (imageFileName?: string) => void;
};

const ImageSelectionSection: React.FC<ImageSelectionSectionProperties> = ({
  imageFileName,
  onImageUpdate,
}) => {
  const { processId } = useParams();

  const environment = useEnvironment();

  const [reloadParam, setReloadParam] = useState(0);

  const [api, contextHolder] = notification.useNotification();

  const fallbackImage =
    'data:image/png;base64,';

  const imageURL =
    imageFileName &&
    `/api/private/${environment.spaceId}/processes/${processId as string}/images/${imageFileName}?${reloadParam}`;

  const showImageUploadFailMessage = () => {
    api.info({
      message: `Image upload failed`,
      description: 'Something went wrong while uploading the selected image.',
      placement: 'topRight',
    });
  };
  return (
    <>
      {contextHolder}
      <Image
        src={imageURL || fallbackImage}
        alt="Image"
        fallback={fallbackImage}
        style={{
          width: '100%',
          height: '100%',
          borderRadius: '6px',
          border: '1px solid #d9d9d9',
        }}
        preview={{
          visible: false,
          mask: (
            <div>
              <Upload
                style={{ color: 'white' }}
                showUploadList={false}
                beforeUpload={() => false} // needed for custom upload of file
                onChange={async ({ fileList }) => {
                  const uploadFile = fileList.pop(); // get latest uploaded file
                  if (uploadFile && uploadFile.originFileObj) {
                    // scale down image to max length of 1500px if file size is over 2mb
                    const image =
                      uploadFile.originFileObj.size > 2000000
                        ? await scaleDownImage(uploadFile.originFileObj, 1500)
                        : uploadFile.originFileObj;

                    if (imageFileName) {
                      // Update existing image
                      try {
                        const response = await fetch(
                          `/api/private/${environment.spaceId}/processes/${processId as string}/images/${imageFileName}`,
                          {
                            method: 'PUT',
                            body: image,
                          },
                        );

                        if (!response.ok) {
                          showImageUploadFailMessage();
                        } else {
                          setReloadParam(Date.now());
                        }
                      } catch (err) {
                        showImageUploadFailMessage();
                      }
                    } else {
                      // Add new Image
                      try {
                        const response = await fetch(
                          `/api/private/${environment.spaceId}/processes/${processId as string}/images/`,
                          {
                            method: 'POST',
                            body: image,
                          },
                        );
                        if (!response.ok) {
                          showImageUploadFailMessage();
                        } else {
                          const newImageFileName = await response.text();
                          onImageUpdate(newImageFileName);
                        }
                      } catch (err) {
                        showImageUploadFailMessage();
                      }
                    }
                  }
                }}
              >
                <span style={{ color: 'white' }}>
                  {imageURL ? <EditOutlined></EditOutlined> : 'Add Image'}
                </span>
              </Upload>
              {imageURL && (
                <DeleteOutlined
                  style={{ marginLeft: '0.5rem' }}
                  onClick={async () => {
                    await fetch(
                      `/api/private/${environment.spaceId}/processes/${processId as string}/images/${imageFileName}`,
                      {
                        method: 'DELETE',
                      },
                    );
                    onImageUpdate();
                  }}
                ></DeleteOutlined>
              )}
            </div>
          ),
        }}
        role="group"
        aria-label="image-section"
      ></Image>
    </>
  );
};

export default ImageSelectionSection;
