'use client';

import type Modeling from 'bpmn-js/lib/features/modeling/Modeling';

import type { ElementLike } from 'diagram-js/lib/core/Types';

import useModelerStateStore from '@/lib/use-modeler-state-store';

import React, { FocusEvent, useEffect, useState } from 'react';

import { Card, Input, ColorPicker, Drawer, Space, Image } from 'antd';

import { EuroCircleOutlined, ClockCircleOutlined } from '@ant-design/icons';

type PropertiesPanelProperties = {
  selectedElement: ElementLike;
  setOpen: (open: boolean) => void;
};

const PropertiesPanel: React.FC<PropertiesPanelProperties> = ({ selectedElement, setOpen }) => {
  const [name, setName] = useState('');

  const modeler = useModelerStateStore((state) => state.modeler);

  useEffect(() => {
    if (selectedElement) {
      setName(selectedElement.businessObject.name);
      console.log(selectedElement);
    }
  }, [selectedElement]);

  const handleNameChange = (event: FocusEvent<HTMLInputElement>) => {
    const modeling = modeler!.get('modeling') as Modeling;
    modeling.updateProperties(selectedElement as any, { name: event.target.value });
    setName('');
  };

  const propPanel = (
    <Drawer
      title={name}
      placement="right"
      onClose={(e) => {
        setOpen(false);
      }}
      open={true}
      mask={false}
    >
      <Space direction="vertical">
        <Space direction="vertical">
          <b>General</b>
          <Input
            addonBefore="Name"
            placeholder={selectedElement.businessObject.name}
            value={name}
            // onChange={(e) => setName(e.target.value)}
            onBlur={handleNameChange}
            disabled={selectedElement.type === 'bpmn:Process'}
          />
          <Input
            addonBefore="ID &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;"
            placeholder={selectedElement.id}
            disabled
          />
        </Space>

        <Space direction="vertical">
          <b>Image</b>
          <Image
            width={200}
            height={200}
            src="error"
            fallback="data:image/png;base64,"
            alt="No Image"
          />
        </Space>

        <Space direction="vertical">
          <b>Properties</b>
          <Input
            prefix={<EuroCircleOutlined className="clock-icon" />}
            placeholder="Planned Cost"
          />
          <Input
            prefix={<ClockCircleOutlined className="clock-icon" />}
            placeholder="Planned Duration"
          />
        </Space>
        {selectedElement.type !== 'bpmn:Process' && (
          <Space direction="vertical">
            <b>Colors</b>
            <Space>
              <ColorPicker
                presets={[
                  {
                    label: 'Recommended',
                    colors: [
                      '#000000',
                      '#000000E0',
                      '#000000A6',
                      '#00000073',
                      '#00000040',
                      '#00000026',
                      '#0000001A',
                      '#00000012',
                      '#0000000A',
                      '#00000005',
                      '#F5222D',
                      '#FA8C16',
                      '#FADB14',
                      '#8BBB11',
                      '#52C41A',
                      '#13A8A8',
                      '#1677FF',
                      '#2F54EB',
                      '#722ED1',
                      '#EB2F96',
                      '#F5222D4D',
                      '#FA8C164D',
                      '#FADB144D',
                      '#8BBB114D',
                      '#52C41A4D',
                      '#13A8A84D',
                      '#1677FF4D',
                      '#2F54EB4D',
                      '#722ED14D',
                      '#EB2F964D',
                    ],
                  },
                  {
                    label: 'Recent',
                    colors: [],
                  },
                ]}
              />
              <span>Background Colour</span>
            </Space>
            <Space>
              <ColorPicker
                presets={[
                  {
                    label: 'Recommended',
                    colors: [
                      '#000000',
                      '#000000E0',
                      '#000000A6',
                      '#00000073',
                      '#00000040',
                      '#00000026',
                      '#0000001A',
                      '#00000012',
                      '#0000000A',
                      '#00000005',
                      '#F5222D',
                      '#FA8C16',
                      '#FADB14',
                      '#8BBB11',
                      '#52C41A',
                      '#13A8A8',
                      '#1677FF',
                      '#2F54EB',
                      '#722ED1',
                      '#EB2F96',
                      '#F5222D4D',
                      '#FA8C164D',
                      '#FADB144D',
                      '#8BBB114D',
                      '#52C41A4D',
                      '#13A8A84D',
                      '#1677FF4D',
                      '#2F54EB4D',
                      '#722ED14D',
                      '#EB2F964D',
                    ],
                  },
                  {
                    label: 'Recent',
                    colors: [],
                  },
                ]}
              />
              <span>Stroke Colour</span>
            </Space>
          </Space>
        )}
      </Space>
    </Drawer>
  );

  return (
    <>
      {/* <Card
        title={selectedElement.id}
        style={{ position: 'absolute', top: 150, right: 20, zIndex: 20 }}
        onClick={(event) => event.preventDefault()}
      >
        Name:{' '}
        <Input value={name} onChange={(e) => setName(e.target.value)} onBlur={handleNameChange} />
      </Card> */}
      {propPanel}
    </>
  );
};

export default PropertiesPanel;
